import React from "react";
import "../styles/NoteView.Module.css";
import NoteHeader from "./NoteHeader";
import Input from "./Input";

const NoteView = ({ name, color, id, isMobile, display, setDisplay }) => {
  const [notes, setNotes] = React.useState([]);
  const [groupId, setGroupId] = React.useState("");
  const [newNote, setNewNote] = React.useState({});

  React.useEffect(() => {
    const noteGroups = JSON.parse(localStorage.getItem("noteGroups"));
    const groupIndex = noteGroups.findIndex((group) => group.id === id);
    if (groupIndex === -1) {
      console.error(`Group with ID ${id} not found`);
      return;
    }
    const group = noteGroups[groupIndex];
    setGroupId(group.id);
    setNotes([...group.notes], newNote);
  }, [id, newNote]);

  const handleNewNote = (value) => {
    // Add timestamp to the new note
    const timestampedNote = {
      ...value,
      timestamp: new Date().toISOString() // Save current timestamp
    };
    setNewNote(timestampedNote);
    setNotes([...notes, timestampedNote]);
  };

  // Function to format the date and time from a timestamp
  const formatDateTime = (timestamp) => {
    try {
      const date = new Date(timestamp);
      if (isNaN(date.getTime())) {
        throw new Error('Invalid date');
      }
      const dateOptions = { day: 'numeric', month: 'short', year: 'numeric' };
      const timeOptions = { hour: 'numeric', minute: 'numeric', hour12: true };

      const formattedDate = new Intl.DateTimeFormat('en-GB', dateOptions).format(date);
      const formattedTime = new Intl.DateTimeFormat('en-GB', timeOptions).format(date);

      return { date: formattedDate, time: formattedTime };
    } catch (error) {
      console.error('Error formatting date and time:', error);
      return { date: 'Invalid date', time: 'Invalid time' }; // Fallback
    }
  };

  return (
    <div
      className="note-view-container"
      style={{ display: isMobile && !display ? "none" : "" }}
    >
      <NoteHeader
        name={name}
        color={color}
        isMobile={isMobile}
        display={display}
        setDisplay={setDisplay}
      />
      {groupId === id &&
        notes &&
        notes.map((note, index) => {
          const { date, time } = formatDateTime(note.timestamp); // Use note's timestamp

          return (
            <div className="note-box" key={index}>
              <div className="note-content">{note.content}</div>
              <div className="note-footer">
                <div className="note-date-time">
                  <span className="note-date">{date}</span>
                  <span className="bullet">â€¢</span>
                  <span className="note-time">{time}</span>
                </div>
              </div>
            </div>
          );
        })}
      {notes && notes.length <= 0 && (
        <p className="example-txt">Start Writing Notes Here!</p>
      )}
      <Input id={id} handleNewNote={handleNewNote} />
    </div>
  );
};

export default NoteView;
