import React, { useState, useEffect } from "react";
import "../styles/Home.Module.css";
import homeImg from "../assets/home.png";

const Home = ({ noteBtnClick, isMobile }) => {
  const [noteGroups, setNoteGroups] = useState(
    JSON.parse(localStorage.getItem("noteGroups")) || []
  );
  const [filteredGroups, setFilteredGroups] = useState(noteGroups);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedNote, setSelectedNote] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    const storedNoteGroups = JSON.parse(localStorage.getItem("noteGroups")) || [];
    setNoteGroups(storedNoteGroups);
    setFilteredGroups(storedNoteGroups);
  }, [noteBtnClick]);

  const handleSearch = () => {
    const filtered = noteGroups.filter((group) =>
      group.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      group.notes.some((note) =>
        note.content.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
    setFilteredGroups(filtered);
  };

  const handleShareNote = (note) => {
    setSelectedNote(note);
  };

  const handleShareGroup = (group) => {
    setSelectedGroup(group);
  };

  const handleCloseShare = () => {
    setSelectedNote(null);
    setSelectedGroup(null);
  };

  const handleShare = (type, data) => {
    // Implement share logic (e.g., copy to clipboard, share via email, etc.)
    alert(`Shared ${type}: ${data}`);
    handleCloseShare();
  };

  return (
    <div className={`home ${noteGroups.length > 0 && isMobile ? "none" : "flex"}`}>
      <img src={homeImg} alt="img" width="400px" className="home-img" />
      <p className="home-title">Sticky Notes</p>
      <p className="home-des">
        Send and receive messages without keeping your phone online. Use Sticky
        Notes on up to 4 linked devices and 1 mobile phone
      </p>
      <div className="search-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search by group name or note content..."
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {filteredGroups.map((group) => (
        <div key={group.id}>
          <h3>{group.name}</h3>
          <button onClick={() => handleShareGroup(group)}>Share Group</button>
          {group.notes.map((note) => (
            <div key={note.id}>
              <p>{note.content}</p>
              <button onClick={() => handleShareNote(note)}>Share Note</button>
            </div>
          ))}
        </div>
      ))}
      {selectedNote && (
        <div className="share-note-modal">
          <p>Share Note</p>
          <button onClick={() => handleShare('note', selectedNote.content)}>Share</button>
          <button onClick={handleCloseShare}>Close</button>
        </div>
      )}
      {selectedGroup && (
        <div className="share-group-modal">
          <p>Share Group</p>
          <button onClick={() => handleShare('group', selectedGroup.name)}>Share</button>
          <button onClick={handleCloseShare}>Close</button>
        </div>
      )}
    </div>
  );
};

export default Home;
